(()=>{const e=document.getElementById("playerBoard"),d=document.getElementById("computerBoard");for(let t=0;t<121;t++){const o=document.createElement("div");if(0===t)o.style.backgroundColor="#dfdfdf";else if(t>=0&&t<=10){const e=String.fromCharCode(t+64);o.appendChild(document.createTextNode(e)),o.classList.add("letterTile")}else t%11==0?(o.appendChild(document.createTextNode(t/11)),o.classList.add("numberTile")):o.classList.add("boardTile");d.appendChild(o),e.appendChild(o.cloneNode(!0))}})();